mapper.select:
select count(*) from movie

	where genre like #{keyword}


	where grade like #{keyword}


	where actor like #{keyword}

select * from movie

	where genre like #{keyword}


	where grade like #{keyword}


	where actor like #{keyword}

order by num desc

mapper.update:
update movie set
title = #{title}, nation = #{nation},continent=#{continent}, genre = #{genre}, grade = #{grade}, actor = #{actor}
where num = #{num}

mapper.select:
